+"use client"
+
+import { useEffect } from "react";
+import Link from "next/link"
+import { usePathname } from "next/navigation"
+
+
+const Links = [
+    {
+        name: "Home",
+        path: '/',
+    },
+    {
+        name: "About Me",
+        path: "/about",
+    },
+    {
+        name: "Services",
+        path: "/services",
+    },
+    {
+        name: "Work",
+        path: "/work",
+    },
+    {
+        name: "Contact",
+        path: "/contact",
+    },
+];
+
+const Nav = () => {
+    const pathname = usePathname();
+
+  return (
+    <nav className="flex gap-6">  
+        {Links.map((LinkItem, index) => {
+             const isActive = pathname === LinkItem.path;
+           return (
+        <Link 
+            href={LinkItem.path} 
+            key={indexLinkItem.path}
+            className={`${
+                isActive ? "text-white border-b-2 border-purple-500" : "text-gray-300"
+              } px-4 py-2 text-lg capitalize transition-all hover:text-white`}
+            >
+          {LinkItem.name}
+        </Link>
+
+        );
+    })}
+    </nav>
+  );
+};
+
+export default Nav;
